mapper.select:
  		select recipe.recipe_num,recipe.recipe_name,recipe.recipe_category,recipe.servings,recipe.cook_image,recipe.readcount , sel.rec from recipe full outer join
		(select count(*)as rec , recipe_num from bookmark where id=#{id} group by recipe_num) sel on sel.recipe_num = recipe.recipe_num

  			where recipe_name like #{keyword}


  			where recipe_category like #{keyword}

select count(*) from recipe

	where recipe_name like #{keyword}


	where recipe_category like #{keyword}

select * from recipe_content
where recipe_num=#{recipenum}
order by recipe_content_num asc

select recipe.recipe_num,recipe.recipe_name,recipe.recipe_category,recipe.servings,recipe.cook_image,recipe.readcount, sel.rec from recipe join recipe_food food on recipe.recipe_num = food.recipe_num 
full outer join (select count(*)as rec , recipe_num from bookmark where id=#{id} group by recipe_num) sel on sel.recipe_num = recipe.recipe_num where food_name like #{keyword}
order by recipe_num

select * from recipe join bookmark on recipe.recipe_num = bookmark.recipe_num
where id =#{id} 

	and recipe_category =#{keyword}
